import React from "react";
import { Nav } from "./Nav";
import closeButton from "../assets/close.svg";

const Sidebar = ({ show }) => {
  const styles = {
    customStyle: {
      display: show ? "block" : "none",
    },
  };

  return (
    <aside className="sidebar" style={styles.customStyle.display}>
      <img src={closeButton} alt="close-menu" className="close-icon" />
      <Nav isInHeder={false} />
    </aside>
  );
};

export default Sidebar;
